  def getHtml(lang: String): String ={
    if (lang == "en"){
      name = name.replaceAll("Выпуск", "Publication")
      html = html
        .replaceAll("Вы приобрели", "You purchased")
        .replaceAll("Техническая поддержка по вопросам онлайн покупки журнала", "Technical support for online magazine purchase")
    }
    else if (lang == "cn"){
      name = name.replaceAll("Выпуск", "出版物")
      html = html
        .replaceAll("Вы приобрели", "你购买了")
